import { FaSearch } from "react-icons/fa";
import { MdClear } from "react-icons/md";
export default () => {
  const [value, setValue] = useState(true);
  return (
    <div id="search" className={`input-group ${value ? "active" : ""}`}>
      <span
        className="ttsearch_button"
        onClick={() => {
          let bo = document.getElementsByTagName("body");
          value
            ? bo[0].classList.add("search-open")
            : bo[0].classList.remove("search-open");
          setValue(!value);
        }}
      >
        <i className="icon-search">
          <FaSearch />
        </i>
        <i className="icon-close">
          <MdClear />
        </i>
      </span>
    </div>
  );
};
