import { FaSearch } from "react-icons/fa";
import { MdClear } from "react-icons/md";
import { useState } from "react";
import hola from "../../pages";
export default () => {
  const [value, setValue] = useState(true);

  function handlerClass() {
    let bo = document.getElementsByTagName("body");
    if (value) bo[0].classList.add("search-open");
    else {
      bo[0].classList.remove("search-open");
      bo[0].classList.remove("user-open");
    }
    setValue(!value);
  }
  return (
    <div id="search" className={`input-group ${!value ? "active" : ""}`}>
      <span className="ttsearch_button" onClick={() => handlerClass()}>
        <i className="icon-search">
          <FaSearch />
        </i>
        <i className="icon-close">
          <MdClear />
        </i>
      </span>

      <style jsx>{`
        .search {
          display: inline-block;
          float: none;
          margin: 8px 0;
          position: relative;
          vertical-align: top;
        }
        .ttsearch {
          display: inline-block;
          float: none;
          position: relative;
          vertical-align: top;
        }
        .ttsearch_button {
          float: left;
          width: 100%;
        }
        #search .ttsearch_button .icon-search {
          color: #fff;
          float: left;
          font-size: 24px;
          height: 100%;
          line-height: 40px;
          position: relative;
          transition: transform 360ms ease-in-out 0s,
            -webkit-transform 360ms ease-in-out 0s;
          -webkit-transition: transform 360ms ease-in-out 0s,
            -webkit-transform 360ms ease-in-out 0s;
          -moz-transition: transform 360ms ease-in-out 0s,
            -webkit-transform 360ms ease-in-out 0s;
          -o-transition: transform 360ms ease-in-out 0s,
            -webkit-transform 360ms ease-in-out 0s;
          -ms-transition: transform 360ms ease-in-out 0s,
            -webkit-transform 360ms ease-in-out 0s;
          width: 100%;
        }
        #search .ttsearch_button .icon-close {
          color: #fff;
          display: none;
          float: left;
          font-size: 26px;
          height: 100%;
          line-height: 40px;
          transition: all 400ms ease-in-out 0s;
          -webkit-transition: all 400ms ease-in-out 0s;
          -moz-transition: all 400ms ease-in-out 0s;
          -o-transition: all 400ms ease-in-out 0s;
          -ms-transition: all 400ms ease-in-out 0s;
          width: 100%;
        }
        #search .ttsearch_button:hover .icon-search {
          transform: rotateY(180deg);
          -webkit-transform: rotateY(180deg);
          -moz-transform: rotateY(180deg);
          -o-transform: rotateY(180deg);
          -ms-transform: rotateY(180deg);
        }
        #search.active .ttsearch_button .icon-search {
          display: none;
        }
        #search.active .ttsearch_button .icon-close {
          display: block;
        }
        .ttsearchtoggle {
          display: none;
          height: 41px;
          padding: 0;
          position: absolute;
          right: 0;
          top: 80px;
          width: 320px;
          z-index: 99;
        }
        .ttsearchtoggle .input-group-btn {
          float: right;
        }
        #search {
          width: 40px;
          height: 40px;
          cursor: pointer;
          float: left;
        }
        #search .select-wrapper {
          max-width: 160px;
        }
        #search .select-wrapper select {
          height: 40px;
          margin: 0 -1px 0 0;
        }
        #search .select-wrapper::after {
          padding: 3px;
        }
        #search .input-lg {
          float: right;
          height: 46px;
          line-height: 20px;
          -webkit-border-radius: 8px;
          -moz-border-radius: 8px;
          -khtml-border-radius: 8px;
          border-radius: 8px;
          padding: 5px 35px 5px 10px;
          width: 100%;
          background: transparent none repeat scroll 0 0;
          border: none;
          color: #000;
          font-weight: 400;
          font-size: 14px;
          transition: all 500ms ease-in-out 0s;
          -webkit-transition: all 500ms ease-in-out 0s;
          -moz-transition: all 500ms ease-in-out 0s;
          -o-transition: all 500ms ease-in-out 0s;
          -ms-transition: all 500ms ease-in-out 0s;
          letter-spacing: 1px;
          border: 1px solid #e5e5e5;
          border-top: 3px solid #ffd835 !important;
          background: #ffffff none repeat scroll 0 0;
        }
        #search .form-control::-moz-placeholder {
          color: #666;
          text-indent: 0.625rem;
        }
        #search .form-control {
          box-shadow: none;
          -moz-box-shadow: none;
          -webkit-box-shadow: none;
        }
        #search .btn-lg {
          font-size: 13px;
          color: #666;
          line-height: 18px;
          padding: 0;
          width: 30px;
          height: 30px;
          background: none;
          border: none;
          position: absolute;
          left: auto;
          right: 5px;
          top: -35px;
          left: auto;
          z-index: 9999;
          border-radius: 0;
          -moz-border-radius: 0;
          -webkit-border-radius: 0;
          -khtml-border-radius: 0;
        }
        #search .btn-lg .material-icons.icon-search {
          line-height: 24px;
          font-size: 22px;
        }
        #search .btn-lg:hover {
          color: #111111;
        }
        #search .btn-lg::before {
          display: none;
        }
        @media (max-width: 991px) {
          .ttsearchtoggle {
            top: 73px;
          }
        }
        @media (max-width: 767px) {
          #search .input-lg {
            background: #fff;
          }
          .ttsearchtoggle {
            top: 65px;
          }
          .ttsearchtoggle::after {
            right: 15px;
          }
        }

        @media (max-width: 480px) {
          #search {
            position: static;
          }
        }
      `}</style>
    </div>
  );
};
