import { FaUser } from "react-icons/fa";
export default () => (
  <>
    <li className="account-nav dropdown header_user_info">
      <a
        href="/"
        title="Account"
        className="dropdown-toggle"
        data-toggle="dropdown"
      >
        <span className="ttuserheading">
        <i className ="material-icons"><FaUser /></i>
          
        </span>
      </a>
      <ul className="dropdown-menu dropdown-menu-right account-link-toggle">
        <li>
          <a href="/">
            <i className="material-icons">lock</i> Login
          </a>
        </li>
        <li>
          <a href="/">
            <i className="material-icons reg-person">person</i> Register
          </a>
        </li>
        <li>
          <a href="/" id="wishlist-total" title="Wish List (0)">
            <i className="material-icons favorite">favorite</i>{" "}
            <span className="hidden-sm hidden-md">Wish List (0)</span>
          </a>
        </li>
        <li className="ttlanguage">
          <div className="pull-left">
            <form
              action="/"
              method="post"
              enctype="multipart/form-data"
              id="form-language"
            >
              <div className="btn-group">
                <button className="btn btn-link">
                  <img
                    src="./Your Store_files/en-gb.png"
                    alt="English"
                    title="English"
                  />
                  <span className="hidden-xs hidden-sm hidden-md">
                    Language
                  </span>
                </button>
                <ul>
                  <li>
                    <button
                      className="btn btn-link btn-block language-select"
                      type="button"
                      name="en-gb"
                    >
                      <img
                        src="./Your Store_files/en-gb.png"
                        alt="English"
                        title="English"
                      />{" "}
                      English
                    </button>
                  </li>
                  <li>
                    <button
                      className="btn btn-link btn-block language-select"
                      type="button"
                      name="ar-lb"
                    >
                      <img
                        src="./Your Store_files/ar-lb.png"
                        alt="Arabic"
                        title="Arabic"
                      />{" "}
                      Arabic
                    </button>
                  </li>
                </ul>
              </div>
              <input type="hidden" name="code" value="" />
              <input
                type="hidden"
                name="redirect"
                value="https://demo.templatetrip.com/Opencart/OPCTM02/OPCTM051_organic/OPC04/index.php?route=common/home"
              />
            </form>
          </div>
        </li>
        <li className="ttcurrency">
          <div className="pull-left">
            <form
              action="https://demo.templatetrip.com/Opencart/OPCTM02/OPCTM051_organic/OPC04/index.php?route=common/currency/currency"
              method="post"
              enctype="multipart/form-data"
              id="form-currency"
            >
              <div className="btn-group">
                <button className="btn btn-link">
                  {" "}
                  <strong>$</strong>{" "}
                  <span className="hidden-xs hidden-sm hidden-md">
                    Currency
                  </span>
                </button>
                <ul>
                  <li>
                    <button
                      className="currency-select btn btn-link btn-block"
                      type="button"
                      name="EUR"
                    >
                      € Euro
                    </button>
                  </li>
                  <li>
                    <button
                      className="currency-select btn btn-link btn-block"
                      type="button"
                      name="GBP"
                    >
                      £ Pound Sterling
                    </button>
                  </li>
                  <li>
                    <button
                      className="currency-select btn btn-link btn-block"
                      type="button"
                      name="USD"
                    >
                      $ US Dollar
                    </button>
                  </li>
                </ul>
              </div>
              <input type="hidden" name="code" value="" />
              <input
                type="hidden"
                name="redirect"
                value="https://demo.templatetrip.com/Opencart/OPCTM02/OPCTM051_organic/OPC04/index.php?route=common/home"
              />
            </form>
          </div>
        </li>
      </ul>
    </li>

    <style jsx>{`


    .header-top-right .dropdown .dropdown-toggle > i::before { 
	content:"";
}
.header_user_info .dropdown-toggle > span.ttuserheading {
	font-size:24px;
	width: 40px;
	height: 40px;
    float: left;
    color: #fff;
}
.header_user_info .dropdown-toggle > span.ttuserheading::after {
	/* content: '\e7ff'; */
	font-family: 'Material Icons';
	font-size: 26px;
	right: 0;
	left: 0;
	margin: auto;
	top: 0;
	color: #fff;
	position: absolute;
	float: left;
	line-height: 40px;
	width:40px;
	height: 40px;
	text-align: center;
	transition: transform 360ms ease-in-out 0s, -webkit-transform 360ms ease-in-out 0s;
	-webkit-transition: transform 360ms ease-in-out 0s, -webkit-transform 360ms ease-in-out 0s;
	-moz-transition: transform 360ms ease-in-out 0s, -webkit-transform 360ms ease-in-out 0s;
	-o-transition: transform 360ms ease-in-out 0s, -webkit-transform 360ms ease-in-out 0s;
	-ms-transition: transform 360ms ease-in-out 0s, -webkit-transform 360ms ease-in-out 0s;
	z-index: 15;
}
.header_user_info:hover .dropdown-toggle > span.ttuserheading::after{
	transform: rotateY(180deg);
	-webkit-transform: rotateY(180deg);
	-moz-transform: rotateY(180deg);
	-o-transform: rotateY(180deg);
